import{_ as t,c as i,o as e,aj as a}from"./chunks/framework.VtnL2jmu.js";const g=JSON.parse('{"title":"页面跳转值传递","description":"","frontmatter":{},"headers":[],"relativePath":"Javaee/页面跳转值传递.md","filePath":"Javaee/页面跳转值传递.md"}'),l={name:"Javaee/页面跳转值传递.md"};function r(n,s,d,h,p,o){return e(),i("div",null,[...s[0]||(s[0]=[a('<h1 id="页面跳转值传递" tabindex="-1">页面跳转值传递 <a class="header-anchor" href="#页面跳转值传递" aria-label="Permalink to “页面跳转值传递”">​</a></h1><h4 id="_1-使用-request-setattribute-forward-请求转发" tabindex="-1"><strong>1. 使用 <code>request.setAttribute</code> + <code>forward</code>（请求转发）</strong> <a class="header-anchor" href="#_1-使用-request-setattribute-forward-请求转发" aria-label="Permalink to “1. 使用 request.setAttribute + forward（请求转发）”">​</a></h4><ul><li><p><strong>值传递</strong>：</p><ul><li>在 Servlet 中通过 <code>request.setAttribute(&quot;key&quot;, value)</code> 将数据存入 <code>request</code> 对象。</li><li>使用 <code>request.getRequestDispatcher(&quot;target.jsp&quot;).forward(request, response)</code> 转发请求到目标页面。</li><li>此时，<code>request</code> 对象的属性会传递到目标页面。</li></ul></li><li><p><strong>值接收</strong>：</p><ul><li>在 JSP 中，直接使用 <code>${key}</code> 表达式获取传递的值。</li></ul></li><li><p><strong>适用场景</strong>：</p><ul><li>当需要在同一个请求中跳转页面并传递数据时使用。</li></ul></li><li><p><strong>代码示例</strong>：</p><div class="language-java"><button title="Copy Code" class="copy"></button><span class="lang">java</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> Servlet</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">request.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">setAttribute</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;msg&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;提示:注册成功&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">request.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">getRequestDispatcher</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;index.jsp&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">).</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">forward</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(request, response);</span></span></code></pre></div><div class="language-jsp"><button title="Copy Code" class="copy"></button><span class="lang">jsp</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>&lt;p&gt;&lt;span&gt;${msg}&lt;/span&gt;&lt;/p&gt;</span></span></code></pre></div></li></ul><hr><h4 id="_2-使用-url-参数传递-sendredirect-重定向" tabindex="-1"><strong>2. 使用 URL 参数传递 + <code>sendRedirect</code>（重定向）</strong> <a class="header-anchor" href="#_2-使用-url-参数传递-sendredirect-重定向" aria-label="Permalink to “2. 使用 URL 参数传递 + sendRedirect（重定向）”">​</a></h4><ul><li><p><strong>值传递</strong>：</p><ul><li>在 Servlet 中通过 URL 参数将数据附加到重定向的目标地址。</li><li>使用 <code>response.sendRedirect(&quot;target.jsp?key=value&quot;)</code> 进行页面跳转。</li></ul></li><li><p><strong>值接收</strong>：</p><ul><li>在 JSP 中，使用 <code>${param.key}</code> 获取 URL 参数的值。</li></ul></li><li><p><strong>适用场景</strong>：</p><ul><li>当需要跨请求（如重定向）传递数据时使用。</li></ul></li><li><p><strong>代码示例</strong>：</p><div class="language-java"><button title="Copy Code" class="copy"></button><span class="lang">java</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// Servlet</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">response.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sendRedirect</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;index.jsp?msg=&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> +</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> URLEncoder.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">encode</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;提示:注册成功&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;UTF-8&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">));</span></span></code></pre></div><div class="language-JSP"><button title="Copy Code" class="copy"></button><span class="lang">JSP</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>&lt;!--jsp页面  --&gt;</span></span>\n<span class="line"><span>&lt;p&gt;&lt;span&gt;${param.msg}&lt;/span&gt;&lt;/p&gt;</span></span></code></pre></div></li></ul><hr><h3 id="区别" tabindex="-1">区别 <a class="header-anchor" href="#区别" aria-label="Permalink to “区别”">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th><code>request.setAttribute</code> + <code>forward</code></th><th>URL 参数 + <code>sendRedirect</code></th></tr></thead><tbody><tr><td><strong>数据存储范围</strong></td><td>单次请求内有效</td><td>通过 URL 参数传递，跨请求有效</td></tr><tr><td><strong>适用场景</strong></td><td>同一次请求内页面跳转</td><td>页面间或跨请求的跳转和数据传递</td></tr><tr><td><strong>安全性</strong></td><td>数据不暴露在 URL 中，较安全</td><td>数据暴露在 URL 中，安全性较低</td></tr><tr><td><strong>刷新影响</strong></td><td>页面刷新不重复提交请求</td><td>页面刷新会重复发送 URL 参数请求</td></tr><tr><td><strong>表达式</strong>（JSP 获取）</td><td><code>${key}</code></td><td><code>${param.key}</code></td></tr></tbody></table><p>选择哪种方式取决于具体需求：</p><ul><li>如果是同一次请求内的页面跳转，建议使用 <code>request.setAttribute</code> + <code>forward</code>。</li><li>如果需要跨请求或带参数的页面跳转，使用 URL 参数 + <code>sendRedirect</code> 更合适。</li></ul>',11)])])}const c=t(l,[["render",r]]);export{g as __pageData,c as default};
