import{_ as a,c as e,o as n,aj as i}from"./chunks/framework.VtnL2jmu.js";const k=JSON.parse('{"title":"WSL 环境下 Docker 的安装、使用与问题解决","description":"","frontmatter":{},"headers":[],"relativePath":"docker/WSL2安装.md","filePath":"docker/WSL2安装.md"}'),l={name:"docker/WSL2安装.md"};function t(r,s,o,p,c,d){return n(),e("div",null,[...s[0]||(s[0]=[i(`<h1 id="wsl-环境下-docker-的安装、使用与问题解决" tabindex="-1">WSL 环境下 Docker 的安装、使用与问题解决 <a class="header-anchor" href="#wsl-环境下-docker-的安装、使用与问题解决" aria-label="Permalink to “WSL 环境下 Docker 的安装、使用与问题解决”">​</a></h1><h2 id="一般步骤" tabindex="-1">一般步骤 <a class="header-anchor" href="#一般步骤" aria-label="Permalink to “一般步骤”">​</a></h2><h4 id="_1-安装-docker" tabindex="-1"><strong>1. 安装 Docker</strong> <a class="header-anchor" href="#_1-安装-docker" aria-label="Permalink to “1. 安装 Docker”">​</a></h4><ul><li><p><strong>推荐方案</strong>：使用 <strong>Docker Desktop + WSL 2 后端</strong>（官方推荐，自动集成） 安装 Docker Desktop 时勾选 <strong>&quot;Use WSL 2 based engine&quot;</strong>。 在 WSL 中直接运行 <code>docker</code>命令，无需额外配置。</p></li><li><p><strong>备用方案</strong>：在 WSL 内手动安装 Docker 引擎（仅限 WSL 2）</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>sudo apt update &amp;&amp; sudo apt install -y docker.io</span></span>
<span class="line"><span>sudo service docker start</span></span></code></pre></div></li></ul><h4 id="_2-验证-wsl-和-docker" tabindex="-1"><strong>2. 验证 WSL 和 Docker</strong> <a class="header-anchor" href="#_2-验证-wsl-和-docker" aria-label="Permalink to “2. 验证 WSL 和 Docker”">​</a></h4><ul><li><p><strong>检查 WSL 状态</strong>：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>wsl -l -v              # 查看已安装的发行版及版本</span></span>
<span class="line"><span>wsl --status           # 检查 WSL 运行状态</span></span></code></pre></div></li><li><p><strong>验证 Docker</strong>：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>docker --version       # 检查版本</span></span>
<span class="line"><span>docker run hello-world # 运行测试容器</span></span></code></pre></div></li></ul><h4 id="_3-进入容器的正确方式" tabindex="-1"><strong>3. 进入容器的正确方式</strong> <a class="header-anchor" href="#_3-进入容器的正确方式" aria-label="Permalink to “3. 进入容器的正确方式”">​</a></h4><ul><li><p><strong>推荐命令</strong>：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>docker exec -it 容器名 /bin/sh   # 精简镜像（如 Alpine）</span></span>
<span class="line"><span>docker exec -it 容器名 /bin/bash # 完整镜像（如 Ubuntu）</span></span></code></pre></div></li><li><p><strong>常见问题</strong>： 如果报错 <code>bash: not found</code>，说明容器使用 <code>sh</code>，改用 <code>/bin/sh</code>。 极简镜像可能无 Shell，需通过 <code>docker cp</code>或自定义 Dockerfile 解决。</p></li></ul><h4 id="_4-文件传输到容器" tabindex="-1"><strong>4. 文件传输到容器</strong> <a class="header-anchor" href="#_4-文件传输到容器" aria-label="Permalink to “4. 文件传输到容器”">​</a></h4><ul><li><p><strong>方法 1：挂载目录（实时同步）</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>docker run -v /mnt/c/Windows/path:/app 镜像名</span></span></code></pre></div></li><li><p><strong>方法 2：<code>docker cp</code>复制文件</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>docker cp ./file.txt 容器名:/path/</span></span></code></pre></div></li><li><p><strong>方法 3：通过 Dockerfile 构建镜像</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>COPY file.txt /app/</span></span></code></pre></div></li></ul><h4 id="_5-常见错误解决" tabindex="-1"><strong>5. 常见错误解决</strong> <a class="header-anchor" href="#_5-常见错误解决" aria-label="Permalink to “5. 常见错误解决”">​</a></h4><ul><li><p><strong>Docker 安装失败</strong>：</p><ul><li><p>手动启用 Windows 功能（WSL 2、Hyper-V）：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>dism.exe /online /enable-feature /featurename:Microsoft-Windows-Subsystem-Linux /all /norestart</span></span>
<span class="line"><span>dism.exe /online /enable-feature /featurename:VirtualMachinePlatform /all /norestart</span></span></code></pre></div></li><li><p>彻底清理后重装（删除残留的 <code>%APPDATA%\\Docker</code>等目录）。</p></li></ul></li><li><p><strong>文件未找到</strong>： 使用 <code>ls -la</code>代替 <code>ll</code>（精简镜像无此别名）。 全盘搜索文件：<code>find / -name &quot;filename&quot;</code>。</p></li></ul><h4 id="_6-关键命令速查" tabindex="-1"><strong>6. 关键命令速查</strong> <a class="header-anchor" href="#_6-关键命令速查" aria-label="Permalink to “6. 关键命令速查”">​</a></h4><table tabindex="0"><thead><tr><th>功能</th><th>命令示例</th></tr></thead><tbody><tr><td>进入容器</td><td><code>docker exec -it 容器名 /bin/sh</code></td></tr><tr><td>复制文件到容器</td><td><code>docker cp ./file 容器名:/path/</code></td></tr><tr><td>挂载目录到容器</td><td><code>docker run -v /host/path:/container/path 镜像名</code></td></tr><tr><td>查看容器信息</td><td><code>docker inspect 容器名 | grep WorkingDir</code></td></tr><tr><td>安装缺失工具</td><td><code>apk add bash</code>（Alpine）或 <code>apt install bash</code>（Debian）</td></tr></tbody></table><hr><h3 id="最终建议" tabindex="-1"><strong>最终建议</strong> <a class="header-anchor" href="#最终建议" aria-label="Permalink to “最终建议”">​</a></h3><ol><li><strong>开发环境</strong>：优先用 <strong>Docker Desktop + WSL 2</strong>，享受无缝集成。</li><li><strong>生产环境</strong>：通过 Dockerfile 构建镜像，避免依赖本地文件。</li><li><strong>调试技巧</strong>： 精简镜像用 <code>/bin/sh</code>，完整镜像用 <code>/bin/bash</code>。 文件传输失败时，优先检查路径和权限。</li></ol><h2 id="附录" tabindex="-1">附录 <a class="header-anchor" href="#附录" aria-label="Permalink to “附录”">​</a></h2><p>由于<code>Docker Desktop + WSL 2 后端</code>的方式<span style="color:red;">安装失败</span>，多方面缘故，故才用<code>在 WSL 内手动安装 Docker 引擎（仅限 WSL 2）</code>的方式安装，具体如下：</p><h3 id="步骤-1-清理旧安装-如果有" tabindex="-1"><strong>步骤 1：清理旧安装（如果有）</strong> <a class="header-anchor" href="#步骤-1-清理旧安装-如果有" aria-label="Permalink to “步骤 1：清理旧安装（如果有）”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 卸载旧版本 Docker（如果存在）</span></span>
<span class="line"><span>sudo apt remove -y docker docker-engine docker.io containerd runc</span></span>
<span class="line"><span>sudo rm -rf /var/lib/docker</span></span></code></pre></div><h3 id="步骤-2-添加-docker-官方-gpg-密钥和软件源" tabindex="-1"><strong>步骤 2：添加 Docker 官方 GPG 密钥和软件源</strong> <a class="header-anchor" href="#步骤-2-添加-docker-官方-gpg-密钥和软件源" aria-label="Permalink to “步骤 2：添加 Docker 官方 GPG 密钥和软件源”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 安装依赖工具</span></span>
<span class="line"><span>sudo apt update</span></span>
<span class="line"><span>sudo apt install -y ca-certificates curl gnupg lsb-release</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 添加 Docker 官方 GPG 密钥</span></span>
<span class="line"><span>sudo mkdir -p /etc/apt/keyrings</span></span>
<span class="line"><span>curl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo gpg --dearmor -o /etc/apt/keyrings/docker.gpg</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 添加 Docker 官方软件源</span></span>
<span class="line"><span>echo &quot;deb [arch=$(dpkg --print-architecture) signed-by=/etc/apt/keyrings/docker.gpg] https://download.docker.com/linux/ubuntu $(lsb_release -cs) stable&quot; | sudo tee /etc/apt/sources.list.d/docker.list &gt; /dev/null</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 更新软件包索引</span></span>
<span class="line"><span>sudo apt update</span></span></code></pre></div><h3 id="步骤-3-安装-docker-引擎" tabindex="-1"><strong>步骤 3：安装 Docker 引擎</strong> <a class="header-anchor" href="#步骤-3-安装-docker-引擎" aria-label="Permalink to “步骤 3：安装 Docker 引擎”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 安装 Docker CE</span></span>
<span class="line"><span>sudo apt install -y docker-ce docker-ce-cli containerd.io docker-compose-plugin</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 验证安装</span></span>
<span class="line"><span>docker --version</span></span></code></pre></div><h3 id="步骤-4-启动-docker-服务" tabindex="-1"><strong>步骤 4：启动 Docker 服务</strong> <a class="header-anchor" href="#步骤-4-启动-docker-服务" aria-label="Permalink to “步骤 4：启动 Docker 服务”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 启动 Docker 服务</span></span>
<span class="line"><span>sudo service docker start</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 设置开机自启（WSL 需要额外配置，见下文）</span></span>
<span class="line"><span>sudo systemctl enable docker</span></span></code></pre></div><h3 id="步骤-5-测试-docker" tabindex="-1"><strong>步骤 5：测试 Docker</strong> <a class="header-anchor" href="#步骤-5-测试-docker" aria-label="Permalink to “步骤 5：测试 Docker”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 运行测试容器</span></span>
<span class="line"><span>sudo docker run hello-world</span></span></code></pre></div><h2 id="常见问题解决" tabindex="-1"><strong>常见问题解决</strong> <a class="header-anchor" href="#常见问题解决" aria-label="Permalink to “常见问题解决”">​</a></h2><h3 id="问题-1-lsb-release-cs返回错误版本" tabindex="-1"><strong>问题 1：<code>lsb_release -cs</code>返回错误版本</strong> <a class="header-anchor" href="#问题-1-lsb-release-cs返回错误版本" aria-label="Permalink to “问题 1：lsb_release -cs返回错误版本”">​</a></h3><p>如果 <code>$(lsb_release -cs)</code>返回的 Ubuntu 版本不被 Docker 支持（如 <code>jammy</code>但 Docker 还未适配），可以手动指定一个已知兼容版本：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 例如强制使用 focal（Ubuntu 20.04）的仓库</span></span>
<span class="line"><span>echo &quot;deb [arch=amd64 signed-by=/etc/apt/keyrings/docker.gpg] https://download.docker.com/linux/ubuntu focal stable&quot; | sudo tee /etc/apt/sources.list.d/docker.list</span></span></code></pre></div><h3 id="问题-2-gpg-密钥错误" tabindex="-1"><strong>问题 2：GPG 密钥错误</strong> <a class="header-anchor" href="#问题-2-gpg-密钥错误" aria-label="Permalink to “问题 2：GPG 密钥错误”">​</a></h3><p>如果遇到 <code>NO_PUBKEY</code>错误，重新导入密钥：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>sudo rm /etc/apt/keyrings/docker.gpg</span></span>
<span class="line"><span>curl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo gpg --dearmor -o /etc/apt/keyrings/docker.gpg</span></span>
<span class="line"><span>sudo apt update</span></span></code></pre></div><h3 id="问题-3-wsl-2-中-docker-无法自启动" tabindex="-1"><strong>问题 3：WSL 2 中 Docker 无法自启动</strong> <a class="header-anchor" href="#问题-3-wsl-2-中-docker-无法自启动" aria-label="Permalink to “问题 3：WSL 2 中 Docker 无法自启动”">​</a></h3><p>由于 WSL 2 不支持 <code>systemd</code>，需手动配置启动：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 编辑 ~/.bashrc</span></span>
<span class="line"><span>nano ~/.bashrc</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 在文件末尾添加：</span></span>
<span class="line"><span>if ! service docker status &gt;/dev/null 2&gt;&amp;1; then</span></span>
<span class="line"><span>    sudo service docker start &gt;/dev/null 2&gt;&amp;1</span></span>
<span class="line"><span>fi</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 保存后生效</span></span>
<span class="line"><span>source ~/.bashrc</span></span></code></pre></div><hr><h2 id="验证安装" tabindex="-1"><strong>验证安装</strong> <a class="header-anchor" href="#验证安装" aria-label="Permalink to “验证安装”">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># 检查 Docker 版本</span></span>
<span class="line"><span>docker --version</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 检查服务状态</span></span>
<span class="line"><span>sudo service docker status</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 运行测试容器</span></span>
<span class="line"><span>docker run --rm hello-world</span></span></code></pre></div>`,42)])])}const g=a(l,[["render",t]]);export{k as __pageData,g as default};
